import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { getUserFromStorage } from '../services/auth';

export default function Navbar() {
  const user = getUserFromStorage();
  const navigate = useNavigate();

  const logout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <nav className="navbar shadow-md custom-navbar">
      <div className="container d-flex justify-content-between align-items-center">
        
        {/* Brand */}
        <Link className="navbar-brand fw-bold text-white" to="/">
          College Events
        </Link>

        <ul className="navbar-nav ms-auto d-flex align-items-center gap-3">

          {/* Committee items */}
          {user && user.role === 'Committee' && (
            <>
              <li><Link className="nav-link text-white nav-hover" to="/committee/request">Request Event</Link></li>
              <li><Link className="nav-link text-white nav-hover" to="/committee/upcoming">My Upcoming Events</Link></li>
            </>
          )}

          {/* Guest routes */}
          {!user && (
            <>
              <li><Link className="nav-link text-white nav-hover" to="/login">Login</Link></li>
              <li><Link className="nav-link text-white nav-hover" to="/register">Register</Link></li>
            </>
          )}

          {/* Logged in */}
          {user && (
            <>
              <li><Link className="nav-link text-white nav-hover" to="/profile">{user.name}</Link></li>

              {user.role === 'Admin' && (
                <li><Link className="nav-link text-white nav-hover" to="/admin">Dashboard</Link></li>
              )}
              {user.role === 'Committee' && (
                <li><Link className="nav-link text-white nav-hover" to="/committee">Dashboard</Link></li>
              )}
              {user.role === 'User' && (
                <li><Link className="nav-link text-white nav-hover" to="/user">Dashboard</Link></li>
              )}

              <li className="ms-2">
                <button className="btn btn-sm logout-btn" onClick={logout}>
                  Logout
                </button>
              </li>
            </>
          )}

        </ul>
      </div>
    </nav>
  );
}
