import React, { useEffect, useState } from 'react';
import API from '../services/api';

export default function CommitteeDashboard(){
  const [requests,setRequests] = useState([]);

  useEffect(()=>{
    API.get('/events/all').then(r=>{
      // show requests for this committee (server would filter by user in production)
      setRequests(r.data.requests || []);
    }).catch(console.error);
  },[]);

  return (
    <div className="container mt-4">
      <h2>Committee Dashboard</h2>
      <p>Request an Event from the <a href="/committee/request">Request Event</a> page.</p>
      <h4 className="mt-3">My Requests</h4>
      {requests.map(req=> (
        <div key={req._id} className="card mb-2 p-2">
          <h5>{req.title} <span className={`badge ${req.status==='Pending'?'bg-warning': req.status==='Approved'?'bg-success':'bg-danger'}`}>{req.status}</span></h5>
          <p>{req.description}</p>
        </div>
      ))}
    </div>
  )
}
