import React, { useEffect, useState } from 'react';
import API from '../services/api';
import { Link } from 'react-router-dom';

export default function EventList() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    API.get('/events').then(r => setEvents(r.data)).catch(console.error);
  }, []);

  return (
    <div className="container mt-4">
      <h2>Events</h2>
      <div className="row">
        {events.map(ev => (
          <div className="col-md-4 mb-3" key={ev._id}>
            <div className="card h-100">
              <img src={ev.image || 'https://via.placeholder.com/400x200'} className="card-img-top" alt="" />
              <div className="card-body">
                <h5 className="card-title">{ev.title}</h5>
                <p className="card-text">{(ev.description || '').slice(0, 100)}</p>
                <Link to={`/events/${ev._id}`} className="btn btn-primary">Details</Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
