import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getEventById } from '../services/events';
import { registerForEvent } from '../services/registrations';
import { getUserFromStorage } from '../services/auth';
import { getMyRegistrations } from '../services/registrations';
import { toast } from 'react-toastify';

export default function EventDetails(){
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [msg, setMsg] = useState('');
  const [registered, setRegistered] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const user = getUserFromStorage();

  useEffect(()=>{
    const loadEvent = async () => {
      setLoading(true);
      setError(null);
      try {
        if (!id) {
          setError('Invalid event ID');
          setLoading(false);
          return;
        }
        const response = await getEventById(id);
        if (response && response.data) {
          setEvent(response.data);
        } else {
          setError('Event not found');
        }
      } catch (err) {
        console.error('Error loading event:', err);
        const errorMsg = err.response?.data?.message || 'Failed to load event';
        setError(errorMsg);
        toast.error(errorMsg);
      } finally {
        setLoading(false);
      }
    };
    loadEvent();
    // check if user already registered
    if (user && id) {
      getMyRegistrations().then(r=>{
        const regs = r.data || [];
        if (regs.find(x => x.eventId && x.eventId._id === id)) setRegistered(true);
      }).catch(()=>{});
    }
  },[id, user]);

  if(loading) {
    return (
      <div className="container mt-4">
        <div className="text-center py-5">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading event details...</p>
        </div>
      </div>
    );
  }
  
  if(error || !event) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">
          <h4>Event Not Found</h4>
          <p>{error || 'The event you are looking for does not exist or is no longer available.'}</p>
        </div>
      </div>
    );
  }

  const onRegister = async ()=>{
    setMsg('');
    if (registered) { setMsg('You are already registered for this event'); return; }
    try{
      await registerForEvent(event._id);
      setMsg('Registered successfully');
      setRegistered(true);
    }catch(err){
      setMsg(err.response?.data?.message || 'Registration failed');
    }
  }

  return (
    <div className="container mt-4">
      <h2>{event.title || 'Event'}</h2>
      {event.image && (
        <img src={event.image} className="img-fluid mb-3" alt={event.title || 'Event'} style={{maxHeight: '400px', objectFit: 'cover', width: '100%'}} />
      )}
      {event.description && <p>{event.description}</p>}
      {event.registrationNote && (
        <div className="alert alert-warning">
          <strong>Note:</strong> {event.registrationNote}
        </div>
      )}
      <div className="mb-3">
        {event.date && (
          <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
        )}
        {event.time && (
          <p><strong>Time:</strong> {event.time}</p>
        )}
        {event.venue && (
          <p><strong>Venue:</strong> {event.venue}</p>
        )}
        {event.category && (
          <p><strong>Category:</strong> {event.category}</p>
        )}
      </div>
      {event.timeline && event.timeline.length > 0 && (
        <div className="mb-3">
          <h5>Timeline</h5>
          <ul>
            {event.timeline.map((t, i) => (
              <li key={i}><strong>{t.time}</strong> â€” {t.activity}</li>
            ))}
          </ul>
        </div>
      )}
      <div>
        <button className="btn btn-primary" onClick={onRegister} disabled={registered}>{registered ? 'Registered' : 'Register for Event'}</button>
        {msg && <div className="mt-2 alert alert-info">{msg}</div>}
      </div>
    </div>
  )
}
