import React, { useEffect, useState } from 'react';
import { getAllEventsAdmin, approveRequest, rejectRequest } from '../services/events';

export default function AdminDashboard(){
  const [data,setData] = useState({events:[],requests:[]});
  const [loading,setLoading] = useState(false);

  const load = async ()=>{
    try{
      const r = await getAllEventsAdmin();
      setData(r.data);
    }catch(err){ console.error(err); }
  }

  useEffect(()=>{ load(); },[]);

  const onApprove = async (id) => {
    setLoading(true);
    try{ await approveRequest(id); await load(); }catch(e){console.error(e);} finally{setLoading(false)}
  }

  const onReject = async (id) => {
    setLoading(true);
    try{ await rejectRequest(id); await load(); }catch(e){console.error(e);} finally{setLoading(false)}
  }

  return (
    <div className="container mt-4">
      <h2>Admin Dashboard</h2>
      <div className="row">
        <div className="col-md-4"><div className="card p-3">Events: {data.events.length}</div></div>
        <div className="col-md-4"><div className="card p-3">Pending Requests: {data.requests.filter(x=>x.status==='Pending').length}</div></div>
      </div>

      <h3 className="mt-4">Pending Requests</h3>
      {data.requests.map(r=> (
        <div key={r._id} className="card mb-2 p-2">
          <h5>{r.title} <small className="text-muted">by {r.requestedBy?.name}</small></h5>
          <p>{r.description}</p>
          <div>
            <button className="btn btn-success btn-sm me-2" onClick={()=>onApprove(r._id)} disabled={loading}>Approve</button>
            <button className="btn btn-danger btn-sm" onClick={()=>onReject(r._id)} disabled={loading}>Reject</button>
          </div>
        </div>
      ))}
    </div>
  )
}
