import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getEventById } from '../services/events';
import { registerForEvent } from '../services/registrations';
import { getUserFromStorage } from '../services/auth';
import { getMyRegistrations } from '../services/registrations';
import { toast } from 'react-toastify';

export default function EventDetails(){
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [msg, setMsg] = useState('');
  const [registered, setRegistered] = useState(false);
  const [loading, setLoading] = useState(true);
  const user = getUserFromStorage();

  useEffect(()=>{
    const loadEvent = async () => {
      setLoading(true);
      try {
        const response = await getEventById(id);
        setEvent(response.data);
      } catch (err) {
        console.error('Error loading event:', err);
        toast.error(err.response?.data?.message || 'Failed to load event');
      } finally {
        setLoading(false);
      }
    };
    loadEvent();
    // check if user already registered
    if (user) {
      getMyRegistrations().then(r=>{
        const regs = r.data || [];
        if (regs.find(x => x.eventId && x.eventId._id === id)) setRegistered(true);
      }).catch(()=>{});
    }
  },[id, user]);

  if(loading) return <div className="container mt-4">Loading...</div>;
  if(!event) return <div className="container mt-4"><div className="alert alert-danger">Event not found</div></div>;

  const onRegister = async ()=>{
    setMsg('');
    if (registered) { setMsg('You are already registered for this event'); return; }
    try{
      await registerForEvent(event._id);
      setMsg('Registered successfully');
      setRegistered(true);
    }catch(err){
      setMsg(err.response?.data?.message || 'Registration failed');
    }
  }

  return (
    <div className="container mt-4">
      <h2>{event.title}</h2>
      <img src={event.image || 'https://via.placeholder.com/800x300'} className="img-fluid mb-3" alt="" />
      <p>{event.description}</p>
      {event.registrationNote && (
        <div className="alert alert-warning">
          <strong>Note:</strong> {event.registrationNote}
        </div>
      )}
      <p><strong>Date:</strong> {new Date(event.date).toLocaleString()}</p>
      <p><strong>Venue:</strong> {event.venue}</p>
      <div>
        <button className="btn btn-primary" onClick={onRegister} disabled={registered}>{registered ? 'Registered' : 'Register for Event'}</button>
        {msg && <div className="mt-2 alert alert-info">{msg}</div>}
      </div>
    </div>
  )
}
