import React, { useEffect, useState } from 'react';
import { getUserFromStorage } from '../services/auth';
import { getMyRegistrations } from '../services/registrations';

export default function Profile(){
  const user = getUserFromStorage();
  const [regs, setRegs] = useState([]);

  useEffect(()=>{
    getMyRegistrations().then(r=> setRegs(r.data)).catch(console.error);
  },[]);

  if(!user) return <div className="container mt-4">Please login</div>;

  return (
    <div className="container mt-4">
      <h2>Profile</h2>
      <div className="card p-3 mb-3">
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
      </div>

      <h4>My Registrations</h4>
      {regs.length===0 && <p>No registrations yet.</p>}
      {regs.map(r=> (
        <div key={r._id} className="card mb-2 p-2">
          <h5>{r.eventId?.title}</h5>
          <p>{r.eventId?.description}</p>
        </div>
      ))}
    </div>
  )
}
