import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login, saveToken, saveUser } from '../services/auth';

export default function Login() {
  const [form, setForm] = useState({ email: '', password: '', role: 'User' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async e => {
    e.preventDefault();
    setError('');
    try {
      const data = await login(form);
      saveToken(data.token);
      saveUser(data.user);
      // redirect based on role
      if (data.user.role === 'Admin') navigate('/admin');
      else if (data.user.role === 'Committee') navigate('/committee');
      else navigate('/user');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={submit} className="w-50">
        {error && <div className="alert alert-danger">{error}</div>}
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input className="form-control" name="email" value={form.email} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Password</label>
          <input type="password" className="form-control" name="password" value={form.password} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Role</label>
          <select name="role" className="form-select" value={form.role} onChange={onChange}>
            <option>Admin</option>
            <option>Committee</option>
            <option>User</option>
          </select>
        </div>
        <button className="btn btn-primary">Login</button>
      </form>
    </div>
  );
}
