import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../services/auth';

export default function Register() {
  const [form, setForm] = useState({ name: '', email: '', phone: '', password: '', confirm: '', role: 'User' });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const validate = () => {
    if (!form.name?.trim()) return 'Full name is required';
    if (!form.email?.trim()) return 'Email is required';
    // basic email pattern
    const emailRe = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRe.test(form.email)) return 'Enter a valid email';
    if (!form.password || form.password.length < 6) return 'Password must be at least 6 characters';
    if (form.password !== form.confirm) return 'Passwords do not match';
    if (form.role === 'Admin' && !form.adminCode) return 'Admin code is required for Admin role';
    if (form.role === 'Committee' && (!form.committeeName || !form.department || !form.idProof)) return 'Committee name, department and ID proof are required';
    if (form.role === 'User' && (!form.course || !form.year)) return 'Course and year are required for User role';
    return null;
  };

  const submit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    const v = validate();
    if (v) return setError(v);
    setLoading(true);
    try {
      const payload = { name: form.name, email: form.email, phone: form.phone, password: form.password, role: form.role };
      // add role specific fields
      if (form.role === 'Committee') {
        payload.committeeName = form.committeeName;
        payload.department = form.department;
        payload.idProof = form.idProof;
      }
      if (form.role === 'Admin') payload.adminCode = form.adminCode;
      if (form.role === 'User') {
        payload.course = form.course;
        payload.year = form.year;
      }

      await register(payload);
      setSuccess('Registration successful — redirecting to login...');
      setTimeout(() => navigate('/login'), 1400);
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Register</h2>
      <form onSubmit={submit} className="w-75">
        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}
        <div className="mb-3">
          <label className="form-label">Full name</label>
          <input className="form-control" name="name" value={form.name} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input className="form-control" name="email" value={form.email} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Phone</label>
          <input className="form-control" name="phone" value={form.phone} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Password</label>
          <input type="password" className="form-control" name="password" value={form.password} onChange={onChange} />
        </div>
        <div className="mb-3">
          <label className="form-label">Confirm Password</label>
          <input type="password" className="form-control" name="confirm" value={form.confirm} onChange={onChange} />
        </div>

        <div className="mb-3">
          <label className="form-label">Role</label>
          <select name="role" className="form-select" value={form.role} onChange={onChange}>
            <option>Admin</option>
            <option>Committee</option>
            <option>User</option>
          </select>
        </div>

        {form.role === 'Committee' && (
          <>
            <div className="mb-3"><label className="form-label">Committee name</label><input className="form-control" name="committeeName" onChange={onChange} /></div>
            <div className="mb-3"><label className="form-label">Department</label><input className="form-control" name="department" onChange={onChange} /></div>
            <div className="mb-3"><label className="form-label">ID Proof</label><input className="form-control" name="idProof" onChange={onChange} /></div>
          </>
        )}

        {form.role === 'Admin' && (
          <div className="mb-3"><label className="form-label">Admin Code</label><input className="form-control" name="adminCode" onChange={onChange} /></div>
        )}

        {form.role === 'User' && (
          <>
            <div className="mb-3"><label className="form-label">Course</label><input className="form-control" name="course" onChange={onChange} /></div>
            <div className="mb-3"><label className="form-label">Year</label><input className="form-control" name="year" onChange={onChange} /></div>
          </>
        )}

        <button className="btn btn-success" disabled={loading}>{loading ? 'Registering...' : 'Register'}</button>
      </form>
    </div>
  );
}
